# Rainbow text styling
def rainbow_text(text):
    colors = ["#FF0000", "#FF7F00", "#FFFF00", "#00FF00", "#0000FF", "#4B0082", "#8B00FF"]
    styled_text = ""
    for i, char in enumerate(text):
        color = colors[i % len(colors)]
        styled_text += f"<span style='color: {color};'>{char}</span>"
    return styled_text

